mapper.select:
SELECT pro.*, `user`.name, `pte`.name AS processTemplateName,`pty`.name AS processTypeName FROM `oa_process` pro,`sys_user` `user`,`oa_process_template` pte,`oa_process_type` pty WHERE pro.user_id = `user`.id AND pro.`process_template_id` = pte.id AND pro.`process_type_id` = pty.id AND `user`.is_deleted = 0 AND pro.is_deleted = 0 AND pte.is_deleted = 0 AND pty.is_deleted = 0
and (pro.process_code like CONCAT('%',#{vo.keyword},'%') or pro.title like CONCAT('%',#{vo.keyword},'%') or `user`.phone like CONCAT('%',#{vo.keyword},'%') or `user`.name like CONCAT('%',#{vo.keyword},'%'))
and pro.user_id = #{vo.userId}
and pro.status = #{vo.status}
and pro.create_time >= #{vo.createTimeBegin}
and pro.create_time <= #{vo.createTimeEnd}
